import {
  markdownToPdfmake,
  getImageFileName
} from "../../../lib/utils/markdown";

// eslint-disable-next-line max-len
const IMAGE_URL = "https://intmain.co/wp-content/uploads/circuit-breaker-closed-state.png";

describe("markdownToPdfMake", () => {

  describe("test functionality to convert", () => {
    it("should convert markdown to pdfmake", async () => {
      const markdown = "# Header\n Paragraph text";
      const expected = [
        {
          bold: true,
          fontSize: 24,
          id: "header",
          marginBottom: 5,
          nodeName: "H1",
          style: ["html-h1"],
          text: "Header",
        },
        {
          text: " ",
        },
        {
          margin: [0, 5, 0, 10],
          nodeName: "P",
          style: ["html-p"],
          text: " Paragraph text",
        },
        {
          text: " ",
        },
      ];
      const result = await markdownToPdfmake(markdown);
      expect(result).toEqual(expected);
    });

    it("should convert markdown with image URL to pdfmake", async () => {
      const markdown = `![](${IMAGE_URL})`;
      const expected = [
        {
          "nodeName": "P",
          "stack": [
            {
              "nodeName": "IMG",
              // eslint-disable-next-line max-len
              "image": "data:image/png;base64,",
              "maxWidth": 452,
              "style": [
                "html-img",
                "html-p"
              ]
            }
          ]
        },
        {
          "text": " "
        }
      ];
      const result = await markdownToPdfmake(markdown);
      expect(result).toEqual(expected);
    });
  });
});

describe("getImageFileName", () => {
  it("should return the file name from the image URL",
    async () => {
      const fileName = await getImageFileName(IMAGE_URL);
      expect(fileName).toEqual("circuit-breaker-closed-state.png");
    });
});